import React, {useEffect, useState} from "react";
import {connect} from "react-redux";
import {
    addItem,
    removeItem,
    requestEmployees
} from "../../redux/EmployeesPageReducer";
import Employees from "./Employees";


const EmployeesContainer = (props) => {

    useEffect(() => {
        props.requestEmployees(props.per_page)
    }, [props.page])

    return <>
        <Employees total={props.total}
                   per_page={props.per_page}
                   employees={props.employees}
                   removeItem={props.removeItem}
                   addEmployee={props.addEmployee}
        />
    </>
}


const mapStateToProps = (state) => {
    return {
        employees: state.employeesPage.employees,
        per_page: state.employeesPage.per_page,
        total: state.employeesPage.total,
        page: state.employeesPage.page
    }
}


export default connect(mapStateToProps, {requestEmployees, removeItem, addItem})(EmployeesContainer)
