import React from "react";
import Employee from "./Emloyee";
import style from "./employees.module.css"
import {TextField} from "react-final-form-antd";
import {Form, Field} from 'react-final-form'


const Employees = ({total, per_page, removeItem, addEmployee, ...props}) => {

    const employeeElement = props.employees.map((u, index) => <Employee employees={u} id={u.id} removeItem={removeItem}
                                                                        key={index}/>)

    const onSubmit = async value => {
        const name = value.employeetext
        props.addEmployee(name)
    }

    return <div>

        <div>
            <Form
                onSubmit={onSubmit}
                render={({handleSubmit, pristine, submitting, form}) => (
                    <form
                        onSubmit={(event) => {
                            const promise = handleSubmit(event);
                            promise && promise.then(() => {
                                form.reset();
                            })
                            return promise;
                        }}
                    >
                        <div className={style.enterDataTask}>
                            <div className={style.inputTask}>
                                <Field name="employeetext" component={TextField}
                                       placeholder="enter your name"/>
                            </div>

                            <button type="submit" disabled={submitting || pristine} className={style.addTaskButton}>
                                Add employee
                            </button>

                        </div>

                    </form>
                )}
            />
        </div>
        <div className={style.employees}>
            {employeeElement}
        </div>
    </div>


}

export default Employees;
